import { createSlice } from "@reduxjs/toolkit";
import setAuthToken from "../utils/setAuthToken";
import axios from "axios";
const initialState = {
    auth:null,
    token:localStorage.getItem('token'),
    loading:true,
    user:null
}

const authSlice= createSlice({
    name:"auth",
    initialState,
    reducers:{
        register_success(state,action){
            state.auth=true;
            state.loading=false;
            localStorage.setItem('token',action.payload);
        },
        log_out(state,action){
            localStorage.removeItem('token');
            state.auth=false;
            state.loading=false;
            state.user=null;
        },
        user_loaded(state,action){
            state.auth=true;
            state.loading=false;
            state.user=action.payload;
        }
    }
})

export function loadUser(){
    return async function userLoadedThunks(dispatch,getState){
        console.log(getState.token);
        if(localStorage.token){
            setAuthToken(getState.token);
        }
        
        try {
            const res= await axios.get('http://localhost:5000/api/auth');
            dispatch(user_loaded(res.data));
        } catch (err) {
            dispatch(log_out());
        }
    }
}

export const {register_success,log_out,user_loaded} = authSlice.actions;
export default authSlice.reducer;
