import React, { useState } from 'react'

function SignUp() {
    const [error,setError] =useState(null);
    const handleSubmit = (e)=>{
        e.preventDefault();
        const email=document.getElementsByClassName('sign-i2');
        const pass=document.getElementsByClassName('sign-i3');
        if(pass.length<6){
            setError(()=>{
                document.getElementsByClassName('sign-error').classList.remove('hide');
                return "password must be more than 5 characters";
            });
        }
    }
    
    const change = ()=>{
        setError(()=>{
            console.log(document.getElementsByClassName('sign-error'));
            document.getElementsByClassName('sign-error').classList.add('hide');
            return null;
        })
    }
  return (
    <div className='sign'>
        
        <div className="sign-error hide">
            {error}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1' onChange={change} placeholder='Name' required />
            <input type='email' className='inp sign-i2' onChange={change} placeholder='Email' required />
            <input type='password' className='inp sign-i3' onChange={change} placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
