import React from 'react'
import { useDispatch,useSelector } from 'react-redux';
import formatDate from '../../../utils/formatDate';

function Comments({comment,avatar,name }) {
    const {user} = useSelector(state=>state.auth);
    const dispatch=useDispatch();
    const handleDelete=()=>{
        dispatch()
    }
  return (
    <div className='post-item'>
        <div className="post-left">
            <img src={avatar} className='round-img post-avatar' alt='img :/'/>
            <div className="post-name">{name}</div>
        </div>
        <div className="post-content">
            <div className="post-text">
                {comment.text}
            </div>
            <div className='post-rbottom'>
                <div className="post-date">
                    Uploaded on {formatDate(comment.date)}
                </div>
                <div className="post-but">
                    {user._id===comment.user &&
                        <button className="p-del" onClick={handleDelete}>
                            Delete
                        </button>
                    }
                </div>
            </div>
        </div>
    </div>
  )
}

export default Comments
