import {createSlice} from "@reduxjs/toolkit";
import axios from 'axios';
import mongoose from "mongoose";

const initialState = {
    sign_error:[null,null],
}


const signSlice = createSlice({
    name:'sign',
    initialState,
    reducers:{
        pass_error(state,action){
            if(action.payload<=5){
                document.getElementsByClassName('sign-error')[0].classList.remove('hide');
                state.sign_error[0]="password must be greater than 5 characters";
            }
            else{
                state.sign_error="";
                document.getElementsByClassName('sign-error')[0].classList.add('hide');
            }
            
        },
        check_user(state,action){
            state.sign_error[1]="Email already in use";
        }
        
    }
});


   
export function handleSign(name,email,pass){
    return async function handleSignThunk(dispatch,getState){
        try{

            let data={
                name:name,
                email:email,
                password:pass
            };
            
            const res= await axios.post('http://localhost:5000/api/users',data);
            console.log(res);
            dispatch(check_user(res.data));
        }catch(err){

        }
    }
}

export const {pass_error,check_user} = signSlice.actions;
export default signSlice.reducer;
