import {createSlice} from "@reduxjs/toolkit";
import axios from 'axios';

const initialState = {
    sign_error:{},
}


const signSlice = createSlice({
    name:'sign',
    initialState,
    reducers:{
        
        AddError(state,action){
            console.log(action.payload);
            let n=action.payload.length;
            for(let i=0;i<n;i++){
                state.sign_error.push(action.payload[i].msg);
            }
        }
        
    }
});


   
export function handleSign(name,email,pass){
    return async function handleSignThunk(dispatch,getState){
        try{

            let data={
                name:name,
                email:email,
                password:pass
            };
            
            const res= await axios.post('http://localhost:5000/api/users',data);
        }catch(err){
            dispatch(AddError(err.response.data.errors));
        }
    }
}

export const {AddError} = signSlice.actions;
export default signSlice.reducer;
