import {  useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux';
import { pass_error} from '../../redux/authSlice';
import axios from 'axios';




function SignUp() {
   const {sign_error}= useSelector(state=>state.auth);
    const dispatch = useDispatch();
  

    
    
   
    const handleSubmit = async(e)=>{
        e.preventDefault();
        console.log('entered');
        let datas={
            name:'sfadsdf',
            email:"yuasadsdsadfsdsdfsfdsasdfasasrdfg@g.com",
            password:'dsfafdsa'
        }
        
        const {data} = await axios.post('http://localhost:5000/api/users',datas);
        console.log(data);  
        
        
        
    }
    
    const handleChange = (e) =>{
        console.log(e);
    }
  return (
    <div className='sign'>
        
        <div className="hide sign-error">
            {/* {sign_error} */}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1'  placeholder='Name' required />
            <input type='email' className='inp sign-i2' onChange={handleChange} placeholder='Email' required />
            <input type='password' className='inp sign-i3' onChange={handleChange}  placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
