import React, { useState } from 'react'
import {  useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux';
import { pass_error, register_user ,initialState} from '../../redux/authSlice';





function SignUp() {
   const {sign_error}= useSelector(state=>state.auth);
    const dispatch = useDispatch();
    const {good,setGood} = useState(0);
    if(good){
        const name=document.getElementsByClassName('sign-i1')[0].value;
        const email=document.getElementsByClassName('sign-i2')[0].value;
        const pass=document.getElementsByClassName('sign-i3')[0].value;
        register_user(name,email,pass);
        setGood(0);
    }
    const handleSubmit = (e)=>{
        e.preventDefault();
        const name=document.getElementsByClassName('sign-i1')[0].value;
        const email=document.getElementsByClassName('sign-i2')[0].value;
        const pass=document.getElementsByClassName('sign-i3')[0].value;
        dispatch(pass_error(pass.length));  
        setGood(()=>{
            return 1;
        });
    }
    
    const change = ()=>{
        
    }
  return (
    <div className='sign'>
        
        <div className="hide sign-error">
            {sign_error}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1' onChange={change} placeholder='Name' required />
            <input type='email' className='inp sign-i2' onChange={change} placeholder='Email' required />
            <input type='password' className='inp sign-i3' onChange={change} placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
