import React, { useEffect, useState } from 'react'
import { pass_error,handleSign } from '../../reducer/signSlice';
import {useDispatch,useSelector} from 'react-redux'




function SignUp() {
    const {sign_error}  = useSelector(state=>state==state.id);
    const [good,setGood] = useState(0);
    const dispatch = useDispatch();
   
    const handleSubmit = (e)=>{
        e.preventDefault();
        const name=document.getElementsByClassName('sign-i1')[0].value;    
        const email=document.getElementsByClassName('sign-i2')[0].value;    
        const pass=document.getElementsByClassName('sign-i3')[0].value;    
        dispatch(pass_error(pass));
        console.log(email);
        dispatch(handleSign(name,email,pass));
    }
    
  return (
    <div className='sign'>
        
        <div className="hide sign-error">
            {sign_error}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1'  placeholder='Name' required />
            <input type='email' className='inp sign-i2'  placeholder='Email' required />
            <input type='password' className='inp sign-i3'  placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
