import React, { useState } from 'react'
import {  useSelector } from 'react-redux/es/hooks/useSelector';
import { useDispatch } from 'react-redux';
import { pass_error} from '../../redux/authSlice';
import axios from 'axios';




function SignUp() {
   const {sign_error}= useSelector(state=>state.auth);
    const dispatch = useDispatch();
    const [good,setGood] = useState(0);

    const submit =async()=>{
        // const name=document.getElementsByClassName('sign-i1')[0].value;
        // const email=document.getElementsByClassName('sign-i2')[0].value;
        // const password=document.getElementsByClassName('sign-i3')[0].value;
        console.log('entered');
        let datas={
            name:'sfadsdf',
            email:"yuasadsadfsfdsasdfasasrdfg@g.com",
            password:'dsfafdsa'
        }
        
        const {data} = await axios.post('http://localhost:5000/api/users',datas);
        console.log(data);
    }
    
    if(good && sign_error===""){
        setGood(0);
        submit();
    }
    const handleSubmit = async(e)=>{
        e.preventDefault();
        const pass=document.getElementsByClassName('sign-i3')[0].value;
        dispatch(pass_error(pass.length));  
        setGood(()=>{
            return 1;
        });
       
        
        
    }
    
    // const change = ()=>{
        
    // }
  return (
    <div className='sign'>
        
        <div className="hide sign-error">
            {/* {sign_error} */}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1'  placeholder='Name' required />
            <input type='email' className='inp sign-i2'  placeholder='Email' required />
            <input type='password' className='inp sign-i3'  placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
