import {createSlice} from "@reduxjs/toolkit";
import axios from 'axios';
// import { useDispatch } from 'react-redux';

const initialState = {
    sign_error:"hi",
    data:null
}
dispatch= useDispatch();

const authSlice = createSlice({
    name:'auth',
    initialState,
    reducers:{
        pass_error(state,action){
            let status=0;
            if(action.payload<=5){
                document.getElementsByClassName('sign-error')[0].classList.remove('hide');
                state.sign_error="password must be greater than 5 characters";
            }
            else{
                state.sign_error="";
                document.getElementsByClassName('sign-error')[0].classList.add('hide');
            }
            
        },
        check_user(state,action){
            state.data=action.payload;
        }
        
    }
});

export function register_user(name,email,password){
    return async function regThunks(dispatch,getState){
    let data={
        name:name,
        email:email,
        password:password
    }
   

    let response= await axios.post('http://localhost:5000/api/users',JSON.stringify(data),{
        headers: {
            'Content-Type': 'application/json'
        }
    });
    dispatch(check_user(response));
        // console.log(response);
}
    
}
export const {pass_error} = authSlice.actions;
export default authSlice.reducer;
