import {createSlice} from "@reduxjs/toolkit";
import axios from 'axios';

const initialState = {
    sign_error:"hi",

}

const authSlice = createSlice({
    name:'auth',
    initialState,
    reducers:{
        pass_error(state,action){
            let status=0;
            if(action.payload<=5){
                document.getElementsByClassName('sign-error')[0].classList.remove('hide');
                state.sign_error="password must be greater than 5 characters";
            }
            else{
                state.sign_error="";
                document.getElementsByClassName('sign-error')[0].classList.add('hide');
            }
            
        },
        // check_user(state,action){
        //     state.
        // }
        
    }
});

export const register_user = async(name,email,password)=>{
    console.log(name,email,password);
    let response = await axios.post('/api/users',{name:name,email:email,password:password});
    console.log(response);
}
export const {pass_error} = authSlice.actions;
export default authSlice.reducer;
