import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteAccount, getProfile } from '../../reducer/profileSlice';
import { Link } from 'react-router-dom';
import Experience from './Experience';
import Education from './Education';


function Dashboard() {
    const {user} = useSelector(state=>state.auth);
    const {profile,loading} = useSelector(state=>state.profile);
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(getProfile());
    },[dispatch]);
    const handleClick=(e)=>{
        e.preventDefault();
    }
    const createProfile =<>
    <div className="dash-desc2">you have not created Profile</div>
    <Link to={'/dashboard/createProfile'}>
        <button className="but pro-create-but" onClick={handleClick}>Create Profile</button>
    </Link>
    </>
    
    const updateProfile= <>
    <Link to={'/dashboard/editProfile'}>
        <button className="but pro-update-but"onClick={handleClick}>Update Profile</button>
    </Link> 
    <Link to={'/dashboard/experience'}>
        <button className="but pro-exp-but"onClick={handleClick}>Add Experience</button>
    </Link>
    <Link to={'/dashboard/education'}>
        <button className="but pro-edu-but"onClick={handleClick}>Add Education</button>
    </Link>
    </>

    if(loading){
        return <div>Dashboard loading</div>
    }

    const handleDelete=()=>{
        if(window.confirm('Are You Sure Do You Want To Delete Your Account? This also includes Deleting your Post and Profile')===true){
            dispatch(deleteAccount());
        }
    }

  return (
    <div className="dash">
        <div className="hide dash-error">

        </div>
        <div className="dash-head">
            Dashboard
        </div>
        <div className="dash-desc">
            Welcome {user && user.name}
        </div>
        <div className="dash-field">
            {profile===null && createProfile}
            <div className="dash-pro-but">
                {profile!==null && updateProfile}
            </div>
        </div>
        <Experience />
        <Education />
        <button className="but dash-del del-act but-del" onClick={handleDelete}>Delete Account</button>
    </div>
  )
}

export default Dashboard
