import React, { useState } from 'react'
import axios from 'axios';




function SignUp() {

    const [good,setGood] = useState(0);

    const submit =async()=>{
        console.log('entered');
        let datas={
            name:'sfadsdf',
            email:"yuasadsadfsdsdfsfdsasdfasasrdfg@g.com",
            password:'dsfafdsa'
        }
        
        const {data} = await axios.post('http://localhost:5000/api/users',datas);
        console.log(data);
        setGood(0);

    }
    
    if(good ){
        submit();
    }
    const handleSubmit = async(e)=>{
        e.preventDefault();
        setGood(()=>{
            return 1;
        });
       
        
        
    }
    
  return (
    <div className='sign'>
        
        <div className="hide sign-error">
            {/* {sign_error} */}
        </div>
        <div className='sign-head'>
            Sign Up
        </div>
        <div className='sign-desc'>
            Create Your Account
        </div>
        <form className="sign-field">
            <input type='text' className='inp sign-i1'  placeholder='Name' required />
            <input type='email' className='inp sign-i2'  placeholder='Email' required />
            <input type='password' className='inp sign-i3'  placeholder='Password' required />
            <button className="but sign-but" onClick={handleSubmit}>Sign Up</button>
        </form>
    </div>
  );
}

export default SignUp
