import {createSlice} from "@reduxjs/toolkit";

const initialState = [];

const authSlice = createSlice({
    name:'auth',
    initialState,
    reducers:{
        pass_error(state,action){
            if(action.payload<=5){
                console.log(action.payload);
                state.push("password must be greater than 5 characters");
            }
        }
        
    }
});
export const {pass_error} = authSlice.actions;
export default authSlice.reducer;
