import {createSlice} from "@reduxjs/toolkit";

const intialState = [];

const authSlicer = createSlice({
    name:'auth',
    intialState,
    reducers:{
        pass_error(state,action){
            state.push("password must be more than 5 characters");
        }
        
    }
});

export default authSlicer.reducer;
